// import logo from './logo.svg';
import './App.css';
import axios from 'axios';
import CurrencySelector from './CurrencySelector';
import BitcoinRate from './BitcoinRate';


function App() {
  const [currency, setCurrency] = useState('');
  const [bitExchange, setRate] = useState(0);
  function findConversion(currency){
    setCurrency(currency);
    // console.log(currency);
    axios.get('https://api.coindesk.com/v1/bpi/currentprice.json')
      .then(res =>{
        // console.log(res.data.bpi[currency].rate);
        setRate(res.data.bpi[currency].rate);
        console.log(bitExchange);

      })
  }
  return (
    <div className="App">
      <header className="App-header">
        <CurrencySelector getCurrency={findConversion}/>
        <BitcoinRate setExchangeRate={bitExchange} setCurrency={currency}/>

      </header>
    </div>
  );
}

export default App;
